import design from '../src/assets/ui.svg';
import computer from '../src/assets/computer.svg';
import code from '../src/assets/code.svg';

export const roles = [
    {
        id: '1',
        title: 'Software',
        heading: 'Development',
        info: 'Experienced in OOP, Event-Driven and Component-based development: Python, PhP, Javascript, TypeScript',
        svg: computer,
    },
    {
        id: '2',
        title: 'Frontend Dev',
        heading: 'Vue, Angular',
        info: 'Over 2 years experience of development in HTML, CSS. JS, PHP, Vue and Angular frameworks.',
        svg: code,
    },
    {
        id: '3',
        title: 'UX/UI Design',
        heading: 'Figma, XD',
        info: 'Passionate about UI/UX. Over 3 years experience designing with Figma and Adobe XD.',
        svg: design,
    },
];

export const aboutInfo = `
                    <p>As a <span class="key">Frontend Focused Developer</span> and <span class="key">UX/UI Designer</span> 
                    based in Kenya, I get to combine my love for technology with my passion for art and design.</p>
                    <br>
                    <p>I attained my Bachelor’s degree in <span class="key">Software Development</span> from <span class="key">KCA University</span>, and I am currently pursuing the <span class="key">Google UX Design Certificate</span> on <span class="key">Coursera</span>.</p>
                    <br>
                    <p>I enjoy using my creativity and problem-solving skills to create digital experiences that not only look great but also work seamlessly for the user. When Im not designing or coding, you can usually find me exploring the city or my neighbourhood in search for inspiration while taking in the sceneries. I believe that continuous learning is the key to success, and ï'm always on the look out for fresh challenges to keep me motivated and engaged.</p>`;
